Shaders.Vertex:
varying vec3 vUv;

void main() {
	vUv = position;
    float range = 0.6;
    float speed = 0.001;
	vec3 pos = position;
	pos.x += sin(time * speed) * range;

    vec4 modelViewPosition = modelViewMatrix * vec4(pos, 1.0);
	gl_Position = projectionMatrix * modelViewPosition;
}

Shaders.Fragment:
varying vec3 vUv;

void main() {
	gl_FragColor = vec4(mix(colorA, colorB, vUv.z), 1.0);
}
