Shaders.Vertex:
varying vec3 vUv;

void main() {
	vUv = position;
    vec3 pos = position;
	pos.x += sin(time * speed) * range;

    vec4 modelViewPosition = modelViewMatrix * vec4(pos, 1.0);
	gl_Position = projectionMatrix * modelViewPosition;
}

Shaders.Fragment:
varying vec3 vUv;

void main() {

    // Convert from [-1, 1] to [0, 1]
    vec2 mousePos = mousePosition;
    mousePos.x = (mousePos.x + 1.0) / 2.0;
    mousePos.y = (mousePos.y + 1.0) / 2.0;

	gl_FragColor = vec4(mousePos.x, 1.0 - mousePos.y, vUv.y, 1.0);
}
